import React from "react";
import classes from "./MovieDetail.module.css";
import YouTube from "react-youtube";
const opts = {
  height: "300",
  width: "90%",
  playerVars: {
    autoplay: 1,
  },
};
const MovieDetail = (props) => {
  const imageUrl = `https://image.tmdb.org/t/p/w500${props.movie.backdrop_path}`;
  let content;
  if (props.youtubeData.results.length) {
    content = 
    <YouTube
      videoId={props.youtubeData.results[0].key}
      id={props.youtubeData.id}
      className={classes.video}
      iframeClassName={classes.video}
      opts={opts}
    />;
  } else {
    content = <img src={imageUrl} alt={props.movie.name} />;
  }
  return (
    <div className={classes.detail}>
      <div className={classes["detail-left"]}>
        <h1 className={classes.title}>{props.movie.original_title}</h1>
        <h3>Release Date: {props.movie.release_date}</h3>
        <h3>Voted: {props.movie.vote_average}/10</h3>
        <p>{props.movie.overview}</p>
      </div>
      <div className={classes["detail-right"]}>{content}</div>
    </div>
  );
};
export default MovieDetail;
