import React from "react";
import classes from "./Banner.module.css"
const Banner =(props) =>{
    const randomMovie = props.items[
        Math.floor(Math.random() * props.items.length - 1)
    ]
    return (
        <div className={classes.banner}>
        <h1>{randomMovie.overview}</h1>
        <div className={classes.actions}>
          <button>Play</button>
          <button onClick={fetchMovieHttp}>My List</button>
        </div>
      </div>
    )
}
export default Banner
