import React from "react";
import classes from "./MovieList.module.css"
const MovieList = (props) => {
  let imageUrl = "https://image.tmdb.org/t/p/w300";
  if(props.type === "Original") {
   imageUrl = "https://image.tmdb.org/t/p/w200";
  }
  const movieList = props.movies.map((movie, index) => {
    return (
      <li key={movie[index].id} className = {classes["movie-item"]}>
        <img
          src={ props.type === "Original" ? `${imageUrl}${movie[index].poster_path}` : `${imageUrl}${movie[index].backdrop_path}`}
          alt={movie[index].original_title}
        />
      </li>
    );
  });
  
  return (
    <div className={classes["type-content"]}>
      <h2 className={classes.type}>{props.type}</h2>
      <ul className={classes["movie-list"]}>{movieList}</ul>
    </div>
  )
}
export default MovieList
