import React, {useState} from "react";
import MovieDetail from "./MovieDetail ";
import classes from "./MovieList.module.css";


const MovieList = (props) => {
  const [isClick, setIsClick] = useState(false);
  const [showClickedMovie, setShowClickedMovie] = useState([]);
  let imageUrl = "https://image.tmdb.org/t/p/w300";
  if (props.type === "Original") {
    imageUrl = "https://image.tmdb.org/t/p/w200";
  }

  const showDetailHandler = (event) => {
    const clickedMovieIndex =  props.movies.findIndex(item => item.id === Number(event.target.closest("li").dataset.index))
    const clickedMovie = props.movies[clickedMovieIndex]
    setShowClickedMovie(prev => prev.push(clickedMovie))
    if(clickedMovie) {
      const existingMovieIndex = showClickedMovie.findIndex(item=>item.id === Number(event.target.closest("li").dataset.index))
      const existingMovie = showClickedMovie[existingMovieIndex]
      if(existingMovie) {
        setIsClick(false)
      } else {
        setShowClickedMovie(showClickedMovie[existingMovieIndex])
      }
    }
    console.log(showClickedMovie[0])
    
    
  }
    
  
  const movieList = props.movies.map((movie) => {
    return (
      <li
        key={movie.id}
        className={classes["movie-item"]}
        onClick={showDetailHandler}
        data-index = {movie.id}
      >
        <img
          src={
            props.type === "Original"
              ? `${imageUrl}${movie.poster_path}`
              : `${imageUrl}${movie.backdrop_path}`
          }
          alt={movie.original_title}
        />
      </li>
    );
  });

  return (
    <div className={classes["type-content"]}>
      <h2 className={classes.type}>{props.type}</h2>
      <ul className={classes["movie-list"]}>{movieList}</ul>
      {isClick && <MovieDetail movie = {showClickedMovie[0]}/>}
    </div>
  );
};
export default MovieList;
