import React from "react";
import classes from "./MovieList.module.css"
const MovieList = (props) => {
  let imageUrl200 = "https://image.tmdb.org/t/p/w200";
  let imageUrl500 = "https://image.tmdb.org/t/p/w500";
  const imageSrc = props.type === "Original" ? `${imageUrl200}${movie.poster_path}` : `${imageUrl500}${movie.backdrop_path}`
  const movieList = props.movies.map((movie) => {
    return (
      <li key={movie.id} className = {classes["movie-item"]}>
        <img
          src={imageSrc}
          alt={movie.original_title}
        />
      </li>
    );
  });
  
  return (
    <div className={classes["type-content"]}>
      <h2 className={classes.type}>{props.type}</h2>
      <ul className={classes["movie-list"]}>{movieList}</ul>
    </div>
  )
}
export default MovieList
